let playerMove = true;
let enemyMove = true;

document.addEventListener('click', (event) => {
    event.preventDefault();
    if (playerMove === false) {
        let grid = new PF.Grid(matrix);
        let finder = new PF.AStarFinder({
            allowDiagonal: true
        });

        let playerX = Math.round(player.x / cell);
        let playerY = Math.round(player.y / cell);
        let rect = cvs.getBoundingClientRect();
        let targetX = Math.round((event.clientX - rect.left) / cell);
        let targetY = Math.round((event.clientY - rect.top) / cell);
        console.log(targetX);
        console.log(targetY);

        let path = finder.findPath(playerX, playerY, targetX, targetY, grid);
        path.forEach((value, index) => {
            setTimeout(() => {
                if ((value[0] * cell) > player.x) {
                    directionX = 'right'
                } else {
                    directionX = 'left'
                }
                player.x = value[0] * cell;
                player.y = value[1] * cell;
                playerMove = true;
            }, index * 50);
        });
    }
    playerMove = false;
});

function loadSprite(src, callback) {
    let sprite = new Image();
    sprite.src = src;
    sprite.onload = callback;
    return sprite;
}

let settlement = loadSprite('./assets/settlement.jpg', function () {
    let shipLeft = loadSprite('./assets/shipLeft.jpg', function () {
        let pirateLeft = loadSprite('./assets/pirateLeft.jpg', function () {
            let shipRight = loadSprite('./assets/shipRight.jpg', function () {
                let pirateRight = loadSprite('./assets/pirateRight.jpg', function () {
                    function drawGrid() {
                        for (let x = 0; x <= cvs.width; x += cell) {
                            ctx.moveTo(x, 0);
                            ctx.lineTo(x, cvs.height);
                        }

                        for (let y = 0; y <= cvs.height; y += cell) {
                            ctx.moveTo(0, y);
                            ctx.lineTo(cvs.width, y);
                        }

                        ctx.stroke();
                    }

                    function drawShip() {
                        if (directionX === 'left') {
                            currentDirection = 'left'
                        } else if (directionX === 'right') {
                            currentDirection = 'right'
                        }

                        if (currentDirection === 'left') {
                            ctx.drawImage(shipLeft, player.x, player.y);
                        } else if (currentDirection === 'right') {
                            ctx.drawImage(shipRight, player.x, player.y);
                        }
                    }

                    function drawPirate() {
                        if (enemyX === 'left') {
                            enemyDirection = 'left'
                        } else if (enemyY === 'right') {
                            enemyDirection = 'right'
                        }

                        if (enemyDirection === 'left') {
                            ctx.drawImage(pirateLeft, enemy.x, enemy.y);
                        } else if (enemyDirection === 'right') {
                            ctx.drawImage(pirateRight, enemy.x, enemy.y);
                        }
                    }

                    function drawSettlement() {

                    }

                    function movePirate() {
                        if (enemyMove === true) {
                            enemyMove = false;
                            let grid = new PF.Grid(matrix);
                            let finder = new PF.AStarFinder({
                                allowDiagonal: true
                            });

                            let enemyX = Math.round(enemy.x / cell);
                            let enemyY = Math.round(enemy.y / cell);
                            let playerX = Math.round(player.x / cell);
                            let playerY = Math.round(player.y / cell);

                            let path = finder.findPath(enemyX, enemyY, playerX, playerY, grid);
                            path.forEach((value, index) => {
                                setTimeout(() => {
                                    if ((value[0] * cell) > enemy.x) {
                                        directionX = 'right'
                                    } else {
                                        directionX = 'left'
                                    }
                                    enemy.x = value[0] * cell;
                                    enemy.y = value[1] * cell;

                                    if (index === path.length - 1) {
                                        enemyMove = true;
                                    }
                                }, index * 100);
                            });
                        }
                    }

                    function pirateAttack() {
                        let enemyX = Math.round(enemy.x / cell);
                        let enemyY = Math.round(enemy.y / cell);
                        let playerX = Math.round(player.x / cell);
                        let playerY = Math.round(player.y / cell);
                    }

                    function draw() {
                        ctx.clearRect(0, 0, cvs.width, cvs.height);
                        // drawGrid();
                        drawShip();
                        drawPirate();
                        drawSettlement();
                    }

                    function main() {
                        pirateAttack();
                        movePirate();
                    }

                    function pause() {
                        if (!gamePaused) {
                            clearInterval(gameLoop);
                            gamePaused = true;
                        } else {
                            startGame();
                            gamePaused = false;
                        }
                    }

                    function startGame() {
                        gameLoop = setInterval(() => {
                            main();
                            draw();
                        }, 10)
                    }

                    document.getElementById('startGame').addEventListener('click', () => {
                        startGame();
                        document.getElementById('startGame').style.display = 'none';
                        document.getElementById('pauseGame').style.display = 'block';
                        document.getElementById('resumeGame').style.display = 'none';
                    })
                    document.getElementById('pauseGame').addEventListener('click', () => {
                        pause();
                        document.getElementById('pauseGame').style.display = 'none';
                        document.getElementById('resumeGame').style.display = 'block';
                    });
                    document.getElementById('resumeGame').addEventListener('click', () => {
                        pause();
                        document.getElementById('pauseGame').style.display = 'block';
                        document.getElementById('resumeGame').style.display = 'none';
                    });
                })
            })
        });
    });
});